import $ from 'jquery'

export default {
		name: 'Rssfeed',
		props: {
				url : {required: true},
				nsimulpar: {required: false},                                                                            // delimita quantas noticias a partir de idAtual serão exibidas
				refresh: {required: false},
				freezed: {required: false},
		},
	data () {
		return {
			displayNoticias: [],
			bancoNoticias: [],
			idAtual: 0,                                                                                              // aponta para o indice da primeira noticia sendo exibida
			nSimul: 1,                                                                                       // quantidade de noticias exibidas simultaneamente
			refreshtime: 8000,
		}
	},
	created: function(){
		if(this.nsimulpar) this.nSimul = parseInt(this.nsimulpar)                                                 // se nSimulPar for passado, nSimul = nSimulPar
		if(this.refresh) this.refreshtime = parseInt(this.refresh)																					// ideia similar à de cima
		$.ajax({
			url: `api/fetch?feed=${this.url}`,
			dataType: "text"
		}).then((result) => {
			let xml = $.parseXML(result)                                                                     // converte o input em um arquivo xml
			let items = xml.getElementsByTagName("item")                                                     // seleciona todos os itens (noticias)
			for(let i = 0; i < items.length; i++) {                                                   // para cada um deles, gera um objeto com suas informação e adiciona ao banco
				let aux = {
					id: i,
					titulo: items[i].getElementsByTagName("title")[0].childNodes[0].nodeValue,
					descricao: ''
				}
				if(items[i].getElementsByTagName("description")[0].childNodes.length > 0)
					aux.descricao =  items[i].getElementsByTagName("description")[0].childNodes[0].nodeValue.replace(/&nbsp;/g,""), // pega a descrição e remove &nbsp que atrapalham a formatação
				this.bancoNoticias.push(aux)
			}
			if(this.bancoNoticias.length){                                                                    // garante a chamada de update apenas tive ao menos 1 item
				this.update()
			}
		})
	},
	methods: {
		update: function () {
			if(this.freezed)
				return;
			this.displayNoticias = []               // zera o display TODO: talvez seja mais rapido mover no lugar de limpar todos
			for(let i = 0; i < this.nSimul && this.idAtual + i < this.bancoNoticias.length ; i++)
				this.displayNoticias.push(this.bancoNoticias[this.idAtual+i])
			this.idAtual+=this.nSimul;
			if(this.idAtual >= this.bancoNoticias.length)
				this.idAtual = 0;
			setTimeout(this.update, this.refreshtime);
		}
	}
}
